import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './Menu.css'

Menu.propTypes = {

};

function Menu(props) {

    const [Active, SetActive] = useState('install_dotnet')

    const handlerDotNet = (value) => {
        console.log(value)

        SetActive(value)
    }

    return (
        <div className="menu">
            <li className="li_menu" data-toggle="collapse" href="#menu_dotnet" role="button" aria-expanded="true" aria-controls="menu_dotnet">
                <div className="d-flex justify-content-between pr-2">
                    <a className="a_menu a_active">Khóa học C# cơ bản
                    </a>
                    <div className="menu_list"></div>
                </div>
            </li>
            <ul className="collapse menu_dotnet show" id="menu_dotnet">
                <li onClick={() => handlerDotNet('install_dotnet')} 
                    className={Active === 'install_dotnet' ? 'li_detail li_active' : 'li_detail'}>Cài đặt môi trường</li>
                <li onClick={() => handlerDotNet('docs_dotnet')} className="li_detail">Tài liệu tham khảo</li>
            </ul>
            <li className="li_menu">
                <div className="d-flex justify-content-between">
                    <a className="a_menu">Khóa học React cơ bản</a>
                    <div className="menu_list"></div>
                </div>
            </li>
        </div>
    );
}

export default Menu;
