import React from 'react';
import PropTypes from 'prop-types';
import { Route, Switch } from 'react-router-dom';
import MainDotNet from './DotNet/MainDotNet';
import MainReact from './React/MainReact';
import Menu from './Menu/Menu';
import { useSelector } from 'react-redux';
import DotNet from './DotNet/DotNet';

Docs.propTypes = {

};

function Docs(props) {

    const isCheck = useSelector(state => state.Check.isCheck)

    const isActive = useSelector(state => state.Active.isActive)
    
    console.log(isActive)

    return (
        <div className="main_wrapper">
            {
                !isActive ? <Menu /> : ''
            }
            <div className="menu">
            <li className="li_menu" data-toggle="collapse" href="#menu_dotnet" role="button" aria-expanded="true" aria-controls="menu_dotnet">
                <div className="d-flex justify-content-between pr-2">
                    <a className={Active.category === 'dotnet' ? 'a_menu a_active' : 'a_menu'}
                         >
                        Khóa học C# cơ bản
                    </a>
                    <div className="menu_list"></div>
                </div>
            </li>
            <ul className="collapse menu_dotnet show" id="menu_dotnet">
                <Link
                    className={Active.key === 'install_dotnet' ? 'a_detail a_detail_active' : 'a_detail'}
                    to='/react-document/docs/dotnet'>
                    <li onClick={() => handlerDotNet('dotnet', 'install')} 
                    className={Active.key === 'install' ? 'li_detail li_active' : 'li_detail'}>        
                        Cài đặt môi trường
                    </li>
                </Link>
                <Link 
                    className={Active.key === 'docs_dotnet' ? 'a_detail a_detail_active' : 'a_detail'}
                    to='/react-document/docs/dotnet/document'>
                        <li onClick={() => handlerDotNet('dotnet', 'docs')} 
                            className={Active.key === 'docs' ? 'li_detail li_active' : 'li_detail'}>
                                Tài liệu tham khảo
                        </li>                 
                </Link>
                
            </ul>
            <li className="li_menu" data-toggle="collapse" href="#menu_react" role="button" aria-expanded="true" aria-controls="menu_react">
                <div className="d-flex justify-content-between pr-2">
                    <a className={Active.category === 'react'  ? 'a_menu a_active' : 'a_menu'}>
                        Khóa học React cơ bản
                    </a>
                    <div className="menu_list"></div>
                </div>
            </li>
            <ul className="collapse menu_react" id="menu_react">
                <li onClick={() => handlerDotNet('react', 'install_react')} 
                    className={Active.key === 'install_react' ? 'li_detail li_active' : 'li_detail'}>Cài đặt môi trường</li>
                <li onClick={() => handlerDotNet('react', 'docs_react')} 
                    className={Active.key === 'docs_react' ? 'li_detail li_active' : 'li_detail'}>Tài liệu tham khảo</li>
            </ul>
        </div>
            <div className={isCheck ? 'row_wrapper row_wrapper_night' : 'row_wrapper day_header'}>
                <div className="grid_left">

                </div>
                <Switch>

                    <Route exact path='/react-document/docs/dotnet' component={MainDotNet} />
                    <Route path='/react-document/docs/dotnet/document' component={DotNet} />

                    <Route path='/react-document/docs/react' component={MainReact} />

                </Switch>
            </div>
        </div>


    );
}

export default Docs;
